grub.cfg.content:
# GRUB Configuration
 set timeout=5
 set default=0

 menuentry 'Arch Linux' {
    linux /vmlinuz-linux root=UUID=a3f2-4bd9 rw quiet
    initrd /initramfs-linux.img
 }

 menuentry 'Arch Linux (fallback)' {
    linux /vmlinuz-linux root=UUID=a3f2-4bd9 rw
    initrd /initramfs-linux-fallback.img
 }

config.content:
{
    "layer": "top",
    "position": "top",
    "height": 40,
    "modules-left": ["windowland/workspaces", "windowland/window"],
    "modules-center": ["clock"],
    "modules-right": ["cpu", "memory", "network", "pulseaudio", "battery", "tray"],

    "windowland/workspaces": {
        "format": "{icon}",
        "format-icons": {
            "1": "一",
            "2": "二",
            "3": "三",
            "4": "四",
            "5": "五",
            "default": "〇"
        }
    },

    "windowland/window": {
        "max-length": 50
    },

    "clock": {
        "format": "{:%H:%M %a %d %b}",
        "tooltip-format": "<big>{:%Y %B}</big>\\n<tt><small>{calendar}</small></tt>"
    },

    "cpu": {
        "format": " {usage}%",
        "tooltip": false
    },

    "memory": {
        "format": " {}%"
    },

    "network": {
        "format-wifi": " {essid}",
        "format-ethernet": " Connected",
        "format-disconnected": "⚠ Disconnected"
    },

    "pulseaudio": {
        "format": "{icon} {volume}%",
        "format-muted": " Muted",
        "format-icons": {
            "default": ["", "", ""]
        }
    },

    "battery": {
        "states": {
            "warning": 30,
            "critical": 15
        },
        "format": "{icon} {capacity}%",
        "format-icons": ["", "", "", "", ""]
    }
 }

style.css.content:
* {
    font-family: "JetBrains Mono Nerd Font";
    font-size: 13px;
 }

 window#waybar {
    background-color: rgba(30, 30, 46, 0.95);
    border-bottom: 2px solid rgba(203, 166, 247, 0.5);
    color: #cdd6f4;
 }

 #workspaces button {
    padding: 0 10px;
    background-color: transparent;
    color: #89b4fa;
    border-bottom: 2px solid transparent;
 }

 #workspaces button.active {
    background-color: rgba(137, 180, 250, 0.2);
    border-bottom: 2px solid #89b4fa;
 }

 #clock {
    padding: 0 15px;
    color: #cba6f7;
    font-weight: bold;
 }

 #cpu, #memory, #network, #pulseaudio, #battery {
    padding: 0 10px;
 }

 #battery.warning {
    color: #f9e2af;
 }

 #battery.critical {
    color: #f38ba8;
 }

config.rasi.content:
configuration {
    modi: "drun,run,window";
    show-icons: true;
    terminal: "kitty";
    drun-display-format: "{name}";
    disable-history: false;
    sidebar-mode: false;
 }

 @theme "catppuccin-mocha"

